import React, { useContext, useState } from 'react'
import { View, Text } from 'react-native'
import {Picker} from '@react-native-picker/picker';
import SwitchSelector from 'react-native-switch-selector'
import { Context } from '../../App'
import NavigationBar from '../components/NavigationBar'
import { PlayerInfo } from '../types/types';
import { calculateRoles } from '../data/Table';

export default function SettingsPage() {
  const [playerCount, setPlayerCount] = useState(4)
  const context = useContext(Context);
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <View>
            <Text>Mode:</Text>
          </View>
          <View style={{ flexDirection: 'row' }}>
            <SwitchSelector
              initial={0}
              onPress={(value) => context.mode=value as string}
              textColor='black'
              selectedColor='white'
              buttonColor='#cc0066'
              borderColor='black'
              borderRadius={20}
              hasPadding={true}
              style={{width: 200, height: 100}}
              options={[
                { label: 'NORMAL', value: 'normal' },
                { label: 'EXTREME', value: 'extreme' }
              ]}
            />
          </View>
        </View>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'white' }}>
          <View>
            <Text>Number of Players:</Text>
          </View>
          <View style={{ flexDirection: 'row' }}>
            <Picker style={{width: 100}} selectedValue={context.playerCount = playerCount}
              onValueChange={(value) => {setPlayerCount(value as number)}}>
              <Picker.Item key='4' label='4' value={4}/>
              <Picker.Item key='5' label='5' value={5}/>
              <Picker.Item key='6' label='6' value={6}/>
              <Picker.Item key='7' label='7' value={7} />
              <Picker.Item key='8' label='8' value={8} />
              <Picker.Item key='9' label='9' value={9} />
              <Picker.Item key='10' label='10' value={10} />
              <Picker.Item key='11' label='11' value={11} />
              <Picker.Item key='12' label='12' value={12} />
              <Picker.Item key='13' label='13' value={13} />
              <Picker.Item key='14' label='14' value={14} />
              <Picker.Item key='15' label='15' value={15} />
              <Picker.Item key='16' label='16' value={16} />
            </Picker>
          </View>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='StartPage' nextPage='IntroductionPage' callback={function() {return createPlayers(context.playerCount, context.playersInfo)}}></NavigationBar>
      </View>
    </View>
  )
}

function createPlayers(playerCount: number, players:PlayerInfo[]) {
  // const neededRoles = calculateRoles(mode, playerCount)
  while (players.length > 0) {
    players.pop()
  }
  for (let i = 0; i < playerCount; i++) {
    players.push({player:i+1, role: 'Spotless', name:'Player ' + (i+1).toString(), backgroundColor:'grey'})
  }
  return true
}
