import React, { useContext, useState } from 'react'
import { View } from 'react-native'
import PlayersComponent from '../components/PlayersComponent'
import NavigationBar from '../components/NavigationBar'
import PlayerInfoModal from '../components/PlayerInfoModal'
import { AppContext } from '../../App'
import { AppContextType, RoleCount } from '../types/types'
import { calculateRoles } from '../data/Table'
import AlertModal from '../components/AlertModal'

export default function PlayersPage() {
  const appContext = useContext(AppContext);
  const [playerInfoModalVisible, setPlayerInfoModalVisible] = useState(false)
  const [alertModalVisible, setAlertModalVisible] = useState(false);
  const [playerIndex, setPlayerIndex] = useState(0)
  const [playerRole, setPlayerRole] = useState(appContext.playersInfo[playerIndex].role)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9, height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
        {PlayersComponent({setModalVisible: setPlayerInfoModalVisible, setPlayerIndex, setPlayerRole})}
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='RolesPage' nextPage='ItemsPage' callback={function() {return confirmPlayerRoles(appContext, setAlertModalVisible)}}></NavigationBar>
      </View>
      <PlayerInfoModal playerIndex={playerIndex} playerRole={playerRole} setPlayerRole={setPlayerRole} visible={playerInfoModalVisible} setVisible={setPlayerInfoModalVisible}/>
      <AlertModal modalVisible={alertModalVisible} setModalVisible={setAlertModalVisible}/>
    </View>
  )
}

function confirmPlayerRoles(appContext: AppContextType, setAlertModalVisible:React.Dispatch<any>) {
  const countRoles:RoleCount[] = []
  let randomRoles:RoleCount[]
  appContext.neededRoles.forEach(neededRole => {
    countRoles.push({roles: neededRole.roles, count:0})
  });
  if (appContext.mode === 'extreme') {
    // Take the randomRoles and break into individual RoleCounts to keep track of each role count
    const lastArrayItem = countRoles.pop()!
    lastArrayItem.roles.forEach(role => {
      randomRoles.push({roles: [role], count: 0})
    });
  }
  appContext.playersInfo.forEach(playerInfo => {
    const countRole = countRoles.find((countRole) => {return JSON.stringify(countRole.roles) === JSON.stringify([playerInfo.role])})!
    const neededCount = appContext.neededRoles.find((neededRole) => {return JSON.stringify(neededRole.roles) === JSON.stringify([playerInfo.role])})?.count
    // If said role has its needed count, then +1 into one of the randomRoles
    if (countRole.count == neededCount && appContext.mode === 'extreme') {
      const role = randomRoles.roles.find((role) => {return [role] === countRole.roles})!
    } else {
      countRole.count += 1
    }
  });
  if (JSON.stringify(countRoles) === JSON.stringify(appContext.neededRoles)) {
    return true
  } else {
    setAlertModalVisible(true)
    return false
  }
}
