import { useNavigation } from '@react-navigation/native'
import * as Speech from 'expo-speech'
import React, { useContext, useState } from 'react'
import { View, Text, TouchableHighlight, Modal } from 'react-native'
import { AppContext } from '../../App'
import NavigationBar from '../components/NavigationBar'
import PlayersScreen from '../components/PlayersScreen'
import { nightTimeButtonTexts, nightTimeLabelTexts, speechSchoolAnnouncement, speechToBlackenedAwake, speechToBlackenedSleep, speechToMonomi, speechToTraitorsAwake, speechToTraitorsSleep } from '../data/NightTimeDialogue'
import { AppContextType } from '../types/types'

// let modalVisible:boolean = false
let modalType:string = ''
let playerIndex = 0

export default function NightTimePage() {
  const navigation = useNavigation()
  const appContext = useContext(AppContext)
  appContext.dayNumber = 0
  appContext.playersInfo.forEach(playerInfo => { playerInfo.colorScheme = 'white' })
  const [sequence, setSequence] = useState(0)
  const [modalVisible, setModalVisible] = useState(false)
  sequenceAction(sequence, setSequence, modalVisible, setModalVisible, appContext, navigation)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'lightblue' }}>
        <Text>Night Time of Day {appContext.dayNumber}</Text>
      </View>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>{nightTimeLabelTexts(appContext.dayNumber, sequence)}</Text>
        </View>
        <View style={{ flex: 1, flexDirection: 'row', backgroundColor: 'white' }}>
          <TouchableHighlight style={{ width: '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { setSequence(sequence + 1) }}>
            <Text>{nightTimeButtonTexts(appContext.dayNumber, sequence)}</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='ItemsPage' nextPage='MorningTimePage'></NavigationBar>
      </View>
      <Modal visible={modalVisible} style={{ flex: 1 }}>
        <View style={{ flex: 9 }}>
          <PlayersScreen modal={modalType}></PlayersScreen>
        </View>
        <View style={{ flex: 1 }}>
          <TouchableHighlight style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#0066ff' }} 
            onPress={() => { setModalVisible(false) }}>
            <Text>Continue</Text>
          </TouchableHighlight>
        </View>
      </Modal>
    </View>
  )
}

async function sequenceAction(sequence:number, setSequence:React.Dispatch<any>, modalVisible:boolean, setModalVisible:React.Dispatch<any>, appContext:AppContextType, navigation:any) {
  Speech.stop()
  let callback = () => {}
  if (appContext.dayNumber === 0) {
    switch (sequence) {
      case 1: // school announcement
        Speech.speak(speechSchoolAnnouncement, {onDone: () => {
          setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000)
        }})
        break
      case 2: // Traitors wake
        appContext.playersInfo.forEach(playerInfo => {
          if (playerInfo.role === 'Blackened') { playerInfo.colorScheme = 'black' }
          else if (playerInfo.role === 'Traitor') { playerInfo.colorScheme = 'grey' }
        })
        if (modalVisible === false) { setModalVisible(true) }
        Speech.speak(speechToTraitorsAwake, {onDone: () => {
          setTimeout(() => { 
            Speech.speak(speechToTraitorsSleep, {onDone: () => {
              setTimeout(() => { setSequence(sequence + 1) }, 2 * 1000) 
            }})
           }, 5 * 1000) 
        }})
        break
      case 3: // Abilities and items
        if (appContext.mode === 'extreme' && appContext.dayNumber > 0) {
          modalType = 'itemCards'
          if (modalVisible === false) {
            setModalVisible(true)
          }
        } else {
          setSequence(sequence + 2)
        }
        break
      case 4: // wait until modal is closed then move on
        if (modalVisible === false) { setSequence(sequence + 1) }
        break
      case 5: // Monomi protect
        if (modalVisible === false && appContext.mode === 'extreme' && appContext.dayNumber > 0) { // TODO: check if Monomi is still alive
          Speech.speak(speechToMonomi, {onDone: () => {
            setTimeout(() => { 
              setModalVisible(true)
              setSequence(sequence + 1)
            }, 2 * 1000)
          }})
        } else {
          setSequence(sequence + 2)
        }
        break
      case 6: // wait until modal is closed then move on
        if (modalVisible === false) {
          setSequence(sequence + 1)
        }
        break
      case 7: // Blackened attack
        if (modalVisible === true) { setModalVisible(false) }
        Speech.speak(speechToBlackenedAwake)
        break
      case 8: // Navigate to morning
        Speech.speak(speechToBlackenedSleep, {onDone: () => {
          setTimeout(() => { navigation.push('MorningTimePage') }, 2 * 1000) 
        }})
        break
    }
  } else if (appContext.dayNumber === 1) {
    switch (sequence) {
      case 0: // declare abilities/items + show modal
        callback = () => { setTimeout(() => {
          modalType = 'itemCards'
          setModalVisible(true)
          setSequence(sequence + 1)
        }, 1 * 1000) }
        break
      case 1: // wait until modal is closed then move on
        if (modalVisible === false) {
          setSequence(sequence + 1)
        }
        break
      case 2: // make people go to bed 
        callback = () => { setTimeout(() => { setSequence(sequence + 1) }, 2 * 1000) }
        break
      case 3: // wake individuals for night time abilities and items
        if (modalVisible === false) {
          asdf(appContext, setModalVisible, sequence, setSequence)
        }
        break
      case 4: // wake monomi
        Speech.speak(speechToMonomi, {onDone: () => {
          setTimeout(() => { 
            setModalVisible(true)
            setSequence(sequence + 1)
          }, 2 * 1000)
        }})
        break
      case 5: // wait until modal is closed then move on
        if (modalVisible === false) {
          setSequence(sequence + 1)
        }
        break
      case 6:
        navigation.push('MorningTimePage')
        break
    }
  }
  // Say the speech, wait for waitTime, then re-render screen by setting the state "step"
  // Speech.speak(nightTimeSpeeches(appContext.dayNumber, sequence), {onDone: callback})
}

function asdf(appContext:AppContextType, setModalVisible:React.Dispatch<any>, sequence:number, setSequence:React.Dispatch<any>) {
  let speech = ''
  for (let i = playerIndex; i <= appContext.playerCount; i++) {
    if (i === appContext.playerCount) {
      setSequence(sequence + 1)
      return
    }
    if (appContext.playersInfo[i].useItem !== '') {
      playerIndex = i
      break
    }
  }
  if (appContext.playersInfo[playerIndex].useItem === 'Glasses') {
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are on the side of hope or despair.'
  } else if (appContext.playersInfo[playerIndex].useItem === "Someone's Graduation Album") {
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are a traitor or not a traitor.'
  } else if (appContext.playersInfo[playerIndex].useItem === "Silent Receiver") {
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are a spotless or not a spotless.'
  }
  Speech.speak(speech, {onDone: () => setModalVisible(true)})
  playerIndex += 1
}

