import React, { useState } from "react";
import { Modal, View, Text, TouchableHighlight } from "react-native";
import { modalStyles } from "../styles/styles";
import { AppContextType } from "../types/types";

export default function RevealRoleModal({visible, setVisible, appContext, playerIndex, abilityOrItem}:Props) {
  const [confirmationVisible, setConfirmationVisible] = useState(false)
  const [revelationVisible, setRevelationVisible] = useState(false)
  return (
    <Modal animationType="slide" transparent={true} visible={visible}>
      <View style={modalStyles.centeredView}>
        {Confirmation(confirmationVisible, setConfirmationVisible, setRevelationVisible, appContext, playerIndex)}
        {Revelation(revelationVisible, setVisible, appContext, playerIndex, abilityOrItem)}
      </View>
    </Modal>
  )
}

function Confirmation(visible:boolean, setConfirmationVisible:React.Dispatch<any>, setRevelationVisible:React.Dispatch<any>, appContext:AppContextType, playerIndex:number) {
  if (visible) {    
    return (
      <View style={modalStyles.modalView}>
        <Text style={modalStyles.modalText}>Investigate  + {appContext.playersInfo[playerIndex].name}?</Text>
        <View style={{flexDirection: 'row'}}>
          <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
            onPress={() => { 
              setConfirmationVisible(false)
              setRevelationVisible(true)
            }}>
            <Text style={modalStyles.textStyle}>Yes</Text>
          </TouchableHighlight>
          <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }} 
            onPress={() => { setConfirmationVisible(false) }}>
            <Text style={modalStyles.textStyle}>No</Text>
          </TouchableHighlight>
        </View>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function Revelation(visible:boolean, setModalVisible:React.Dispatch<any>, appContext:AppContextType, playerIndex:number, abilityOrItem:string) {
  if (visible) {    
    return (
      <View style={modalStyles.modalView}>
        <Text style={modalStyles.modalText}>{revealText(appContext, playerIndex, abilityOrItem)}</Text>
        <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }} 
          onPress={() => { setModalVisible(false) }}>
          <Text style={modalStyles.textStyle}>OK</Text>
        </TouchableHighlight>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function revealText(appContext:AppContextType, playerIndex:number, abilityOrItem:string):string {
  let string = ''
  switch (abilityOrItem) {
    case "Yasuhiro Hagakure's ability":
      const role = appContext.mode == 'normal' ? 'Despair Disease Patient' : 'Monomi'
      string = appContext.playersInfo[playerIndex].role == role ? role : 'Not ' + role
      break
    case "Glasses":
      if (appContext.playersInfo[playerIndex].role in ['Blackened', 'Traitor']) {
        string = 'Despair'
      } else {
        string = 'Hope'
      }
      break
    case "Someone's Graduation Album":
      string = appContext.playersInfo[playerIndex].role == 'Traitor' ? 'Traitor' : 'Not Traitor'
      break
    case "Silent Receiver":
      string = appContext.playersInfo[playerIndex].role == 'Spotless' ? 'Spotless' : 'Not Spotless'
      break
  }
  return string
}

type Props = {visible:boolean, setVisible:React.Dispatch<any>, appContext:AppContextType, playerIndex:number, abilityOrItem:string}
