import { useNavigation } from '@react-navigation/native';
import * as Speech from 'expo-speech'
import React, { useContext, useState } from 'react'
import { View, Text, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App';
import NavigationBar from '../components/NavigationBar'
import { roleInPlay } from '../data/Table';
import { AppContextType } from '../types/types';

let labelText = 'Good Morning!'
let buttonText = ''

export default function MorningTimeScreen() {
  const appContext = useContext(AppContext)
  const [sequence, setSequence] = useState(0)
  sequenceAction(sequence, setSequence)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'grey' }}>
        <Text>Morning Time of Day {appContext.dayNumber}</Text>
      </View>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>{labelText}</Text>
        </View>
        <View style={{ flex: 1, backgroundColor: 'white' }}>
          <TouchableHighlight style={{ width: '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { setSequence(sequence + 1) }}>
            <Text>{buttonText}</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='NightTimePage' nextPage='DayTimePage'></NavigationBar>
      </View>
    </View>
  )
}

function sequenceAction(sequence:number, setSequence:React.Dispatch<any>) {
  const { push } = useNavigation<any>()
  const appContext = useContext(AppContext)
  // reveal if player has a special role 
  // allow Mikan to investigate dead player's role
  // allow player to play Vice
  Speech.stop()
  Speech.speak(sequence.toString())
  let speech = ''
  switch (sequence) {
    case 0:
      appContext.dayNumber += 1
      Speech.speak(goodMorningSpeech(appContext.dayNumber), {onDone: () => {setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000)}})
      break
    case 1:
      if (appContext.dayNumber === 1) {
        speech = 'Victim, discard one Item card. This attack cannot be negated or deflected. Click the button below when you are done.'
        labelText = 'Discard 1 Item card'
        buttonText = 'Done'
        Speech.speak(speech, {onDone: () => {setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000)}})
      } else {
        setSequence(sequence + 1)
      }
      break
    case 2:
      if (roleInPlay(appContext.roleCounts, 'Monomi') && appContext.dayNumber > 1) {
        speech = 'Did Monomi protect the victim last night?'
        Speech.speak(speech, {onDone: () => {setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000)}})
      } else {
        setSequence(sequence + 1)
      }
      break
    case 3:
      push('DayTimePage')
      break
  }
}

function goodMorningSpeech(dayNumber:number) {
  const days = ['zeroth', 'first', 'second', 'third', 'fourth', 'fifth', 'sixth', 'seventh', 'eight', 'ninth', 'I stopped keeping track']
  return '"Good morning, everyone! It is the morning of the ' + days[dayNumber] + ' day. Get ready to greet another beee-yutiful day"'
}
