import React, { useState } from 'react'
import { View, Text, TouchableHighlight, Modal } from 'react-native'
import NavigationBar from '../components/NavigationBar'
import PlayersComponent from '../components/PlayersComponent'
import PlayersPage from './Players'

export default function NightTimePage() {
  const [text, setText] = useState(defaultText)
  const [step, setStep] = useState(0)
  const [modalVisible, setModalVisible] = useState(false)
  const [playerIndex, setPlayerIndex] = useState(0)
  const [playerRole, setPlayerRole] = useState(0)
  return (
    <View>
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>
            {text}
            {step}
          </Text>
        </View>
        <View style={{ flex: 1, backgroundColor: 'white' }}>
          <TouchableHighlight style={{ width: '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { 
              setStep(step + 1)
              setText(getText(step))
              doSomething(step, setModalVisible)
            }}>
            <Text>Click Me</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='ItemsPage' nextPage=''></NavigationBar>
      </View>
    </View>
    <View>
      <Modal visible={modalVisible} style={{ width: 100}}>
        {PlayersComponent({setModalVisible, setPlayerIndex, setPlayerRole})}
        <NavigationBar previousPage='NightTime' nextPage=''></NavigationBar>
      </Modal>
    </View>
    </View>
  )
}

function doSomething(step:number, setModalVisible:React.Dispatch<any>) {
  if (step === 2) {
    setModalVisible(true)
  }
}

function getText(step:number) {
  let text = ''
  switch (step) {
    case 1:
      text = text1
      break
    case 2:
      text = text2
      break
  }
  return text
}

const defaultText = '“Mm, ahem, this is a school announcement. It is now 10 p.m. As such, it is officially nighttime. \
Okay then… sweet dreams, everyone! Good night, sleep tight, don’t let the bed bugs bite…” \
\n\n \
Press button below when ready'

const text1 = 'Traitors - Wake up. Press the button below to reveal who the Blackened player is'

const text2 = 'Blackened - Wake up. Place the bullet card in front of the player you would like to attack. \
On Night 0, the victim will only lose an item card and not be killed. Press the button below when you are done'
