import { useNavigation } from '@react-navigation/native'
import * as Speech from 'expo-speech'
import React, { useContext, useState } from 'react'
import { View, Text, TouchableHighlight, Modal } from 'react-native'
import { AppContext } from '../../App'
import NavigationBar from '../components/NavigationBar'
import PlayersPage from '../components/PlayersPage'
import { nightTimeButtonTexts, nightTimeLabelTexts, speechSchoolAnnouncement2, speechSchoolAnnouncement1, speechToBlackenedSleep, speechToMonomi, speechToTraitorsAwake, speechToTraitorsSleep, speechToEveryoneSleep, speechToBlackenedAwake1, speechToBlackenedAwake2 } from '../data/NightTimeDialogue'
import { roleInPlay } from '../data/Table'
import { AppContextType } from '../types/types'

// let modalVisible:boolean = false
let modalType:string = ''
let playerIndex = 0
let abilityOrItem = ''

export default function NightTimeScreen() {
  const appContext = useContext(AppContext)
  appContext.playersInfo.forEach(playerInfo => { playerInfo.colorScheme = 'white' })
  const [sequence, setSequence] = useState(0)
  const [playersPageVisible, setPlayersPageVisible] = useState(false)
  sequenceAction(sequence, setSequence, playersPageVisible, setPlayersPageVisible, appContext)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'lightblue' }}>
        <Text>Night Time of Day {appContext.dayNumber}</Text>
      </View>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>{nightTimeLabelTexts(appContext.dayNumber, sequence)}</Text>
        </View>
        <View style={{ flex: 1, flexDirection: 'row', backgroundColor: 'white' }}>
          <TouchableHighlight style={{ width: '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { setSequence(sequence + 1) }}>
            <Text>{nightTimeButtonTexts(appContext.dayNumber, sequence)}</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='ItemsPage' nextPage='MorningTimePage'></NavigationBar>
      </View>
      <Modal visible={playersPageVisible} style={{ flex: 1 }}>
        <View style={{ flex: 9 }}>
          <PlayersPage modal={modalType} abilityOrItem={abilityOrItem}></PlayersPage>
        </View>
        <View style={{ flex: 1 }}>
          <TouchableHighlight style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#0066ff' }} 
            onPress={() => { setPlayersPageVisible(false) }}>
            <Text>Continue</Text>
          </TouchableHighlight>
        </View>
      </Modal>
    </View>
  )
}

function sequenceAction(sequence:number, setSequence:React.Dispatch<any>, modalVisible:boolean, setModalVisible:React.Dispatch<any>, appContext:AppContextType) {
  const { push } = useNavigation<any>()
  Speech.stop()
  Speech.speak(sequence.toString())
  switch (sequence) {
    case 1: // School announcement
      let speech = speechSchoolAnnouncement1
      if (appContext.mode === 'extreme' && appContext.dayNumber > 0) {
        speech = speechSchoolAnnouncement2
      }
      Speech.speak(speech, {onDone: () => { setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000) }})
      break
    case 2: // Declare abilities and items
      if (appContext.mode === 'extreme' && appContext.dayNumber > 0) {
        modalType = 'nightTimeAbilitiesItems'
        if (modalVisible === false) { setModalVisible(true) }
      }
      setSequence(sequence + 1)
      break
    case 3: // Wait until modal is closed then move on
      if (appContext.mode === 'extreme' && appContext.dayNumber > 0) {
        if (modalVisible === false) { 
          Speech.speak(speechToEveryoneSleep, {onDone: () => { setTimeout(() => { setSequence(sequence + 1) }, 1 * 1000) }})
        }
      } else {
        setSequence(sequence + 1)
      }
      break
    case 4: // Wake each person for abilities and items
      if (appContext.mode === 'extreme' && appContext.dayNumber > 0) {
        modalType = 'revealRole'
        if (modalVisible === false) {
          abilitiesAndItemsSpeech(appContext, setModalVisible, sequence, setSequence)
        }
      } else {
        setSequence(sequence + 1)
      }
      break
    case 5: // Traitors wake up
      if (roleInPlay(appContext.roleCounts, 'Traitor') && appContext.dayNumber === 0) {
        appContext.playersInfo.forEach(playerInfo => {
          if (playerInfo.role === 'Blackened') { playerInfo.colorScheme = 'black' }
          else if (playerInfo.role === 'Traitor') { playerInfo.colorScheme = 'grey' }
        })
        if (modalVisible === false) { setModalVisible(true) }
        Speech.speak(speechToTraitorsAwake, {onDone: () => {
          setTimeout(() => {
            Speech.speak(speechToTraitorsSleep, {onDone: () => {
              setTimeout(() => { setSequence(sequence + 1) }, 2 * 1000) 
            }})
          }, 5 * 1000) 
        }})
        break
      } else {
        setSequence(sequence + 1)
      }
    case 6: // Monomi protect
      if (roleInPlay(appContext.roleCounts, 'Monomi') && appContext.dayNumber > 0) { // TODO: check if Monomi is still alive
        if (modalVisible === false) { setModalVisible(true) }
        else { Speech.speak(speechToMonomi, {onDone: () => { setTimeout(() => { setSequence(sequence + 1) }, 2 * 1000)}}) }
      } else {
        setSequence(sequence + 2)
      }
      break
    case 7: // Wait until modal is closed then move on   TODO: maybe Monomi isn't in the group
      if (modalVisible === false) {
        setSequence(sequence + 1)
      }
      break
    case 8: // Blackened attack
      if (modalVisible === true) { setModalVisible(false) }
      else { 
        speech = appContext.dayNumber == 0 ? speechToBlackenedAwake1 : speechToBlackenedAwake2
        Speech.speak(speech) }
      break
    case 9: // Navigate to morning
      Speech.speak(speechToBlackenedSleep, {onDone: () => { setTimeout(() => { push('MorningTimePage') }, 2 * 1000) }})
      break
  }
}

function abilitiesAndItemsSpeech(appContext:AppContextType, setModalVisible:React.Dispatch<any>, sequence:number, setSequence:React.Dispatch<any>) {
  let speech = ''
  if (playerIndex !== 0) {
    speech = appContext.playersInfo[playerIndex - 1].name + ', go to sleep.'
    if (playerIndex === appContext.playerCount) {
      Speech.speak(speech, {onDone: () => { setTimeout(() => { setSequence(sequence + 1) }, 2 * 1000)}})
      return
    }
    Speech.speak(speech)
  }
  for (let i = playerIndex; i <= appContext.playerCount; i++) {
    if (appContext.playersInfo[i].useItem !== '') {
      playerIndex = i
      break
    }
  }
  // TODO: handle using ability and item at same time
  if (appContext.playersInfo[playerIndex].useAbility === "Yasuhiro Hagakure's ability") {
    const role = appContext.playerCount < 7 ? 'the despair disease patient' : 'monomi'
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are ' + role + ' or not ' + role
  }
  if (appContext.playersInfo[playerIndex].useItem === 'Glasses') {
    abilityOrItem = 'Glasses'
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are on the side of hope or despair.'
  } else if (appContext.playersInfo[playerIndex].useItem === "Someone's Graduation Album") {
    abilityOrItem = "Someone's Graduation Album"
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are a traitor or not a traitor.'
  } else if (appContext.playersInfo[playerIndex].useItem === 'Silent Receiver') {
    abilityOrItem = 'Silent Receiver'
    speech = appContext.playersInfo[playerIndex].name + ', wake up. Click the player you would like to investigate whether they \
    are a spotless or not a spotless.'
  }
  appContext.playersInfo[playerIndex].useAbility = ''
  appContext.playersInfo[playerIndex].useItem = ''
  playerIndex += 1
  Speech.speak(speech, {onDone: () => setModalVisible(true)})
}
