import React, { useContext } from 'react'
import { View, Text } from 'react-native'
import { AppContext } from '../../App'
import NavigationBar from '../components/NavigationBar'
import RoleCards from '../components/RoleCards'
import { calculateRoles } from '../data/Table'
import { AppContextType } from '../types/types'

export default function RolesPage() {
  const appContext = useContext(AppContext);
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>
            Prepare the following Role Cards and distribute one card to each player.

            After distributing the Role Cards, have each player enter in their role on the next page
          </Text>
        </View>
        {displayRoleCards(appContext)}
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='IntroductionPage' nextPage='PlayersPage'></NavigationBar>
      </View>
    </View>
  )
}

function displayRoleCards(appContext:AppContextType) {
  const roleCard = appContext.roleCounts.map((neededRole) => 
    <RoleCards key={neededRole.roles.toString()} roles={neededRole.roles} count={neededRole.count}></RoleCards>
  )
  return (
    <View  style={{flex: 4, flexWrap: "wrap", alignItems: 'center', justifyContent: 'space-evenly', backgroundColor: 'white'}}>
      {roleCard}
    </View>
  )
}
