import React, { useContext } from 'react'
import { View, Text, StyleSheet, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App'

export default function PlayersScreen({setModalVisible, setPlayerIndex, setPlayerRole}:Props) {
  return (
  <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
    {PlayersBoxes({setModalVisible, setPlayerIndex, setPlayerRole})}
  </View>
  )
}

function PlayersBoxes({setModalVisible, setPlayerIndex, setPlayerRole}:Props) {
  const context = useContext(AppContext);
  const heightPercent = ((1 / Math.ceil(context.playerCount / 2)) * 100).toString() + '%'
  return (
    [...Array(context.playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={frameStyle(heightPercent).frame}>
        <TouchableHighlight key={'player' + index+1} style={boxStyle(context.playersInfo[index].status).button} onPress={() => {
            setModalVisible(true)
            setPlayerIndex(index)
            setPlayerRole(context.playersInfo[index].role)
          }}>
          <Text style={textStyle(context.playersInfo[index].status).text}>{context.playersInfo[index].name}</Text>
        </TouchableHighlight>
      </View>
    ))
  )
}

type Props = {setModalVisible: React.Dispatch<any>, setPlayerIndex: React.Dispatch<any>, setPlayerRole: React.Dispatch<any>}

function frameStyle(heightPercent: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    }
  })
}

function textStyle(status: string) {
  let textColor = ''
  switch (status) {
    case 'despair':
      textColor = 'white'
      break
    case 'ready':
    case 'notReady':
    case 'hope':
    default:
      textColor = 'black'
      break
  }
  return StyleSheet.create({
    text: {
      color: textColor
    }
  })
}

function boxStyle(status: string) {
  let backgroundColor = ''
  switch (status) {
    case 'ready':
      backgroundColor = 'green'
      break
    case 'despair':
      backgroundColor = 'black'
      break
    case 'notReady':
    case 'hope':
    default:
      backgroundColor = 'white'
      break
  }
  return StyleSheet.create({
    button: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: backgroundColor
    }
  })
}
