import React, { useContext } from "react"
import { Modal, TouchableHighlight, View, Text,StyleSheet } from "react-native"
import { Picker } from '@react-native-picker/picker'
import { AppContext } from "../../App"
import { calculateRoles } from "../data/Table"
import { TextInput } from "react-native-gesture-handler"
import { modalStyles } from "../styles/styles"
import { AppContextType } from "../types/types"

export default function PlayerInfoModal({playerIndex, playerRole, setPlayerRole, visible, setVisible}:Props) {
  const appContext = useContext(AppContext);
  return (
    <Modal animationType='slide' transparent={true} visible={visible}>
      <View style={modalStyles.centeredView}>
        <View style={modalStyles.modalView}>
          <Text style={modalStyles.modalText}>Name:</Text>
          <TextInput style={modalStyles.modalTextInput} placeholder={appContext.playersInfo[playerIndex].name} onChangeText={(text) => appContext.playersInfo[playerIndex].name=text}/>
          <Text style={modalStyles.modalText}>Select Your Role:</Text>
          <Picker style={{width: 200}} selectedValue={playerRole}
            onValueChange={(value) => {setPlayerRole(value)}}>
            {getPickerItems(appContext)}
          </Picker>
          <View style={{flexDirection: 'row'}}>
            <TouchableHighlight
              style={{ ...modalStyles.button, backgroundColor: '#2196F3'}}
              onPress={() => {
                  appContext.playersInfo[playerIndex].role = playerRole
                  appContext.playersInfo[playerIndex].backgroundColor = 'green'
                  setVisible(false)
                }}>
              <Text style={modalStyles.textStyle}>Save</Text>
            </TouchableHighlight>
            <TouchableHighlight
              style={{ ...modalStyles.button, backgroundColor: '#2196F3'}}
              onPress={() => {
                  appContext.playersInfo[playerIndex].name = 'Player ' + (playerIndex + 1).toString()
                  appContext.playersInfo[playerIndex].role = 'Spotless'
                  appContext.playersInfo[playerIndex].backgroundColor = 'grey'
                  setVisible(false)
                }}>
              <Text style={modalStyles.textStyle}>Reset</Text>
            </TouchableHighlight>
            </View>
        </View>
      </View>
    </Modal>
  )
}

function getPickerItems(appContext:AppContextType) {
  const pickerItems:JSX.Element[] = []
  appContext.neededRoles.forEach(neededRole  => {
    if (neededRole.count != 0) {
      neededRole.roles.forEach(role => {
        // if role does not exist in pickerItems, push new pickerItem
        if (pickerItems.find((value) => { return value.key === role }) == undefined) {
          pickerItems.push(<Picker.Item key={role} label={role} value={role}/>)
        }
      })
    }
  });
  return pickerItems
}

type Props = {playerIndex: number, playerRole:string, setPlayerRole: React.Dispatch<any>, visible: boolean, setVisible: React.Dispatch<any>}
