import React, { useContext } from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'
import { Context } from '../App'
import PlayerInfoModal from './PlayerInfoModal'

export default function PlayersComponent({modalVisible, setModalVisible, playerNumber, setPlayerNumber}:Props) {
  const context = useContext(Context);
  return (
    <View>
      <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
        {PlayerBoxes(context.playerCount, modalVisible, setModalVisible, setPlayerNumber)}
      </View>
      <PlayerInfoModal playerNumber={playerNumber} visible={modalVisible} setVisible={setModalVisible}></PlayerInfoModal>
    </View>
  )
}

function PlayerBoxes(playerCount:number, modalVisible: boolean, setModalVisible: React.Dispatch<React.SetStateAction<boolean>>, setPlayer: React.Dispatch<React.SetStateAction<number>>) {
  const heightPercent = ((1 / Math.ceil(playerCount / 2)) * 100).toString() + '%'
  return (
    [...Array(playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={styles(heightPercent).frame}>
        <TouchableOpacity style={styles().button} onPress={() => { setModalVisibility(true, setModalVisible, (index + 1), setPlayer) }}>
          <Text>Player {index + 1}</Text>
        </TouchableOpacity>
      </View>
    ))
  )
}

function setModalVisibility(bool: boolean, setModalVisible: React.Dispatch<React.SetStateAction<boolean>>, player:number, setPlayer: React.Dispatch<React.SetStateAction<number>>) {
  setModalVisible(bool)
  setPlayer(player)
}

type Props = {modalVisible: boolean, setModalVisible: React.Dispatch<React.SetStateAction<boolean>>, playerNumber:number, setPlayerNumber: React.Dispatch<React.SetStateAction<number>>}

function styles(heightPercent?: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    },
    button: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'grey'
    }
  })
}
