import React, { useContext, useState } from 'react'
import { View } from 'react-native'
import { Context } from '../App'
import PlayersComponent from '../components/PlayersComponent'
import NavigationBar from '../components/NavigationBar'
import PlayerInfoModal from '../components/PlayerInfoModal'

export default function PlayersPage() {
  const [modalVisible, setModalVisible] = useState(false);
  const [playerNumber, setPlayerNumber] = useState(1)
  const context = useContext(Context);
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
          {PlayersComponent({modalVisible: modalVisible, setModalVisible: setModalVisible, playerNumber: playerNumber, setPlayerNumber: setPlayerNumber })}
        </View>
        <View>
          <PlayerInfoModal playerNumber={playerNumber} visible={modalVisible} setVisible={setModalVisible}></PlayerInfoModal>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar Previous='RolesPage' Next='ItemsPage'></NavigationBar>
      </View>
    </View>
  )
}
