import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import 'react-native-gesture-handler';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

import StartPage from './app/screens/Start';
import SettingsPage from './app/screens/Settings';
import IntroductionPage from './app/screens/Introduction';
import RolesPage from './app/screens/Roles';
import ItemsPage from './app/screens/Items';
import NightTimePage from './app/screens/NightTime';
import PlayersPage from './app/screens/Players';
import { ContextType, PlayerInfo, RoleCount } from './app/types/types';

export type RootStackParamList = {
  StartPage: undefined
  SettingsPage: undefined
  IntroductionPage: undefined
  RolesPage: {Mode:string, PlayerCount:number}
  ItemsPage: {Mode:string}
  NightTimePage: undefined
  PlayersPage: {PlayerCount:number}
};

const RootStack = createStackNavigator<RootStackParamList>();
export const Context = React.createContext({mode: 'normal', playerCount: 4, playersInfo: []} as ContextType);

export default function App() {
  const [mode, setMode] = useState('normal')
  const [playerCount, setPlayerCount] = useState(4)
  const [neededRoles, setNeededRoles] = useState([] as RoleCount[]);
  const [playersInfo, setPlayers] = useState([] as PlayerInfo[]);
  return (
    <Context.Provider value={{mode: mode, playerCount: playerCount, playersInfo: playersInfo}}>
      <NavigationContainer>
        <RootStack.Navigator>
          <RootStack.Screen
            name="StartPage"
            component={StartPage}
            options={{ title: 'Welcome' }}
          />
          <RootStack.Screen
            name="SettingsPage"
            component={SettingsPage}
            options={{ title: 'Settings' }}
          />
          <RootStack.Screen
            name="IntroductionPage"
            component={IntroductionPage}
            options={{ title: 'Introduction' }}
          />
          <RootStack.Screen
            name="RolesPage"
            component={RolesPage}
            options={{ title: 'Roles' }}
          />
          <RootStack.Screen
            name="PlayersPage"
            component={PlayersPage}
            options={{ title: 'Enter Player Information' }}
          />
          <RootStack.Screen
            name="ItemsPage"
            component={ItemsPage}
            options={{ title: 'Items' }}
          />
          <RootStack.Screen
            name="NightTimePage"
            component={NightTimePage}
            options={{ title: 'Night Time of Day 0' }}
          />
        </RootStack.Navigator>
      </NavigationContainer>
    </Context.Provider>
  );
}
