import React from 'react'
import { View, Text } from 'react-native'
import { Context } from '../App'
import NavigationBar from '../components/NavigationBar'
import RoleCards from '../components/RoleCards'
import { calculateRoles } from '../data/Table'

export default function RolesPage() {
  return (
    <Context.Consumer>{(context) => (
      <View style={{ flex: 1 }}>
        <View style={{ flex: 9 }}>
          <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
            <Text>
              Prepare the following Role Cards and distribute one card to each player
            </Text>
          </View>
          {displayRoleCards(context.mode, context.playerCount)}
        </View>
        <View style={{ flex: 1 }}>
          <NavigationBar Previous='IntroductionPage' Next='PlayersPage'></NavigationBar>
        </View>
      </View>
    )}</Context.Consumer>
  )
}

function displayRoleCards(mode:string, playerCount:number) {
  const roles = calculateRoles(mode, playerCount)
  const roleCard = roles.map((role) => 
    <RoleCards roles={role.role} count={role.count}></RoleCards>
  )
  return (
    <View  style={{flex: 4, flexWrap: "wrap", alignItems: 'center', justifyContent: 'space-evenly', backgroundColor: 'white'}}>
      {roleCard}
    </View>
  )
}
