import React from "react"
import { Modal, TouchableHighlight, View, Text,StyleSheet } from "react-native"
import { Context } from "../App"
import { calculateRoles } from "../data/Table"

export default function PlayerInfoModal({visible, setVisible}:Props) {
  return (
    <Context.Consumer>{(context) => (
      <View style={styles.centeredView}>
        <Modal animationType='slide' transparent={true} visible={visible}>
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              <Text style={styles.modalText}>Select Your Role</Text>
              {listRoles(context.mode, context.playerCount, setVisible)}
            </View>
          </View>
        </Modal>
      </View>
    )}</Context.Consumer>
  )
}

function listRoles(mode:string, playerCount:number, setVisible:React.Dispatch<React.SetStateAction<boolean>>) {
  const roles = calculateRoles(mode, playerCount)
  // roles.map((role) => {
  //   if (role.count != 0) {
      
  //   }
  // })
  const buttons = roles.map((role) => 
    <TouchableHighlight
      style={{ ...styles.openButton, backgroundColor: '#2196F3' }}
      onPress={() => { setVisible(false) }}>
      <Text style={styles.textStyle}>{role.role}</Text>
    </TouchableHighlight>
  )
  return buttons
}

type Props = {visible: boolean, setVisible: React.Dispatch<React.SetStateAction<boolean>>}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  openButton: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center'
  },
    modalText: {
    marginBottom: 15,
    textAlign: 'center'
  }
})
