import React from 'react'
import { View, Text, Image } from 'react-native'

const alterEgo = require('../assets/Role Cards/Alter Ego.png')
const blackened = require('../assets/Role Cards/Blackened.png')
const despairDiseasePatient = require('../assets/Role Cards/Despair Disease Patient.png')
const monomi = require('../assets/Role Cards/Monomi.png')
const spotless = require('../assets/Role Cards/Spotless.png')
const traitor = require('../assets/Role Cards/Traitor.png')
const ultimateDespair = require('../assets/Role Cards/ultimateDespair.png')
const images = {
  alterEgo: { uri: require('../assets/Role Cards/Alter Ego.png') },
  blackened: {
    uri: require('../assets/Role Cards/Blackened.png')
  },
  despairDiseasePatient: {
    uri: require('../assets/Role Cards/Despair Disease Patient.png')
  },
  monomi: {
    uri: require('../assets/Role Cards/Monomi.png')
  },
  spotless: {
    uri: require('../assets/Role Cards/Spotless.png')
  },
  traitor: {
    uri: require('../assets/Role Cards/Traitor.png')
  },
  ultimateDespair: {
    uri: require('../assets/Role Cards/ultimateDespair.png')
  },
}

export default function RoleCards({roles, count}: Props) {
  const thumbnails = roles.map((role) =>
    sources.push(require('../assets/Role Cards/' + role + '.png'))
  )
  // const thumbnails = roles.map((role) =>
  //   <View key={role}>
  //     <Image
  //       style={{width: 50, height: 70}}
  //       source={require(getImageSource(role))}
  //     />
  //     <Text>{role}</Text>
  //   </View>
  // )
  if (count > 0) {
    return (
      <View style={{ flexDirection: 'row', alignItems: 'center', backgroundColor: 'grey' }}>
        {thumbnails}
        <Text style={{ fontSize: 20 }}>x {count}</Text>
      </View>
    )
  } else {
    return (<></>)
  }
}

type Props = {roles:string[], count:number}

function getImageSource(role:string) {
  return '../assets/Role Cards/' + role + '.png'
}
