import { useNavigation } from '@react-navigation/native'
import * as Speech from 'expo-speech'
import React, { useContext, useState } from 'react'
import { View, Text, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App';
import CountdownTimer from '../components/CountdownTimer'
import NavigationBar from '../components/NavigationBar'

export default function DayTimeScreen() {
  const appContext = useContext(AppContext)
  const [sequence, setSequence] = useState(0)
  const [timerVisible, setTimerVisible] = useState(false)
  const [buttonVisible, setButtonVisible] = useState(false)
  const [timerKey, setTimerKey] = useState(0)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'yellow' }}>
        <Text>Day Time of Day {appContext.dayNumber}</Text>
      </View>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>{labelTexts[sequence]}</Text>
          {ShowTimer(timerKey, timerVisible, () => {setButtonVisible(true)})}
        </View>
        <View style={{ flex: 1, flexDirection: 'row', backgroundColor: 'white', alignItems: 'center' }}>
          {ShowButton(buttonVisible, setButtonVisible, timerKey, setTimerKey)}
          <TouchableHighlight style={{ width: buttonVisible == true ? '50%' : '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { sequenceAction(sequence, setTimerVisible, setButtonVisible)}}>
            <Text>{buttonTexts[sequence]}</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='MorningTimePage' nextPage='NightTimePage'></NavigationBar>
      </View>
    </View>
  )
}

function sequenceAction(sequence:number, setTimerVisible:React.Dispatch<any>, setButtonVisible:React.Dispatch<any>) {
  const { push } = useNavigation<any>()
  Speech.stop()
  Speech.speak(sequence.toString())
  switch (sequence) {
    case 0:
      Speech.speak(speeches[0], {onDone: () => {
        setTimeout(() => {sequenceAction(sequence + 1, setTimerVisible, setButtonVisible)}, 1 * 1000)
      }})
      break
    case 1:
      setTimerVisible(true)
      break
    case 2:
      setButtonVisible(false)
      setTimerVisible(false)
      break
    case 3:
      push('NightTimePage')
      break
  }
}

function ShowTimer(timerKey:number, timerVisible:boolean, callback?:() => void) {
  if (timerVisible === true) {
    return (
      <View style={{alignItems: 'center', justifyContent: 'center'}}>
        <CountdownTimer timerKey={timerKey.toString()} duration={10} callback={() => {
          Speech.speak('Time is up.')
          if (callback) { callback() }
          }}></CountdownTimer>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function ShowButton(buttonVisible:boolean, setButtonVisible:React.Dispatch<any>, timerKey:number, setTimerKey:React.Dispatch<any>) {
  if (buttonVisible === true) {
    return (
      <TouchableHighlight style={{ width: '50%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
        onPress={() => { 
          setButtonVisible(false)
          setTimerKey(timerKey + 1)
        }}>
        <Text>Add 3 minutes</Text>
      </TouchableHighlight>
    )
  } else {
    return (
      <></>
    )
  }

}

const labelText1 = ''
const labelText2 = 'Item or Ability?'
const labelText3 = ''
const labelText4 = ''
const labelTexts = [labelText1, labelText2, labelText3, labelText4]

const speech1 = 'Mm, ahem, this is a school announcement. It is now the day time. Please make your way to the briefing room.'
const speech2 = 'Would anybody like to use a day time ability or item? Please declare so now. After everyone who wanted to has declared \
their day time ability or item, click the button below.'
const speech3 = 'Discuss anything you would like, starting now'
const speech4 = ''
const speeches = [speech1, speech2, speech3, speech4]

const buttonText1 = ''
const buttonText2 = 'Continue' 
const buttonText3 = 'End Discussion'
const buttonText4 = 'Move to Night Time'
const buttonTexts = [buttonText1, buttonText2, buttonText3, buttonText4]
