import React, { useContext, useState } from 'react'
import { View, Text, StyleSheet, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App'
import PlayerInfoModal from './PlayerInfoModal';

export default function PlayersScreen({modal}:Props) {
  const appContext = useContext(AppContext);
  const [playerIndex, setPlayerIndex] = useState(0)
  const [modalVisible, setModalVisible] = useState(false)
  const [playerRole, setPlayerRole] = useState(appContext.playersInfo[playerIndex].role)
  return (
    <View>
      <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
        {PlayersBoxes(setPlayerIndex, setPlayerRole, setModalVisible)}
      </View>
      <PlayerInfoModal playerIndex={playerIndex} playerRole={playerRole} setPlayerRole={setPlayerRole} visible={modalVisible} setVisible={setModalVisible}/>
    </View>
  )
}

function PlayersBoxes(setPlayerIndex:React.Dispatch<any>, setPlayerRole:React.Dispatch<any>, setModalVisible?:React.Dispatch<any>|undefined) {
  const appContext = useContext(AppContext);
  const heightPercent = ((1 / Math.ceil(appContext.playerCount / 2)) * 100).toString() + '%'
  return (
    [...Array(appContext.playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={frameStyle(heightPercent).frame}>
        <TouchableHighlight key={'player' + index+1} style={boxStyle(appContext.playersInfo[index].status).style} onPress={() => {
            if(setModalVisible) { setModalVisible(true) }
            setPlayerIndex(index)
            setPlayerRole(appContext.playersInfo[index].role)
          }}>
          <Text style={textStyle(appContext.playersInfo[index].status).style}>{appContext.playersInfo[index].name}</Text>
        </TouchableHighlight>
      </View>
    ))
  )
}

type Props = {modal?:JSX.Element}

function frameStyle(heightPercent: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    }
  })
}

function textStyle(status: string) {
  let textColor = ''
  switch (status) {
    case 'despair':
      textColor = 'white'
      break
    case 'ready':
    case 'notReady':
    case 'hope':
    default:
      textColor = 'black'
      break
  }
  return StyleSheet.create({
    style: {
      color: textColor
    }
  })
}

function boxStyle(status: string) {
  let backgroundColor = ''
  switch (status) {
    case 'ready':
      backgroundColor = 'green'
      break
    case 'despair':
      backgroundColor = 'black'
      break
    case 'notReady':
    case 'hope':
    default:
      backgroundColor = 'white'
      break
  }
  return StyleSheet.create({
    style: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: backgroundColor
    }
  })
}
