import React from "react";
import { Modal, View, Text, TouchableHighlight } from "react-native";

export default function alertModal(alertModalVisible: boolean, setAlertModalVisible:React.Dispatch<any>) {
  return (
    <View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={alertModalVisible}
      >
        <View>
          <View>
            <Text>Hello World!</Text>

            <TouchableHighlight
              style={{ backgroundColor: "#2196F3" }}
              onPress={() => {
                setAlertModalVisible(!alertModalVisible);
              }}
            >
              <Text>Hide Modal</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>

      <TouchableHighlight
        onPress={() => {
          setAlertModalVisible(true);
        }}
      >
        <Text>Show Modal</Text>
      </TouchableHighlight>
    </View>
  )
}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  button: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    marginHorizontal: 5,
    elevation: 2
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center'
  },
    modalText: {
    textAlign: 'center'
  },
  modalTextInput: {
    borderBottomWidth: 1,
    padding: 5,
    marginBottom: 15,
    textAlign: 'center'
  }
})
