import React, { useContext } from 'react'
import { View, Text, Image, Modal, TouchableHighlight } from 'react-native'
import { Context } from '../App'
import NavigationBar from '../components/NavigationBar'

export default function ItemsPage() {
  const context = useContext(Context);
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1 }}>
          <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
            <Text>
              {getText(context.mode)}
            </Text>
          </View>
          <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center', justifyContent: 'center', backgroundColor: 'white' }}>
            {/* <Image
              style={{ width: 100, height: 140 }}
              source={{ uri: require('../assets/Item Cards/Alter Ball.png') }}
            />
            <Image
              style={{ width: 100, height: 140 }}
              source={{ uri: require('../assets/Item Cards/Item Card.jpg') }}
            /> */}
          </View>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='PlayersPage' nextPage='NightTimePage'></NavigationBar>
      </View>
    </View>
  )
}

function getText(mode:string) {
  if (mode === 'extreme') {
    return `
Randomly distribute two Item Cards to each player. Each player has the \
option to return both Item Cards and draw two new Item Cards. You cannot redraw just one Item Card. \
\n \n
You cannot hold more than two cards during the game. If you ever obtain more than two cards, discard \
down to two cards first before continuing.
    `
  } else {
    return `
Since you selected Normal Mode, please skip this section as you will not use Item Cards
    `
  }
}

function alertModal(alertModalVisible: boolean, setAlertModalVisible:React.Dispatch<any>) {
  return (
    <View>
      <Modal
        animationType="slide"
        transparent={true}
        visible={alertModalVisible}
      >
        <View>
          <View>
            <Text>Hello World!</Text>

            <TouchableHighlight
              style={{ backgroundColor: "#2196F3" }}
              onPress={() => {
                setAlertModalVisible(!alertModalVisible);
              }}
            >
              <Text>Hide Modal</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>

      <TouchableHighlight
        onPress={() => {
          setAlertModalVisible(true);
        }}
      >
        <Text>Show Modal</Text>
      </TouchableHighlight>
    </View>
  )
}
