import React, { useContext, useState } from "react"
import { Modal, TouchableHighlight, View, Text,StyleSheet } from "react-native"
import { Picker } from '@react-native-picker/picker';
import { Context } from "../App"
import { calculateRoles } from "../data/Table"
import { TextInput } from "react-native-gesture-handler";

export default function PlayerInfoModal({playerIndex: playerNumber, visible, setVisible}:Props) {
  const context = useContext(Context);
  const [playerRole, setPlayerRole] = useState(context.players[playerNumber - 1].role)
  return (
    <View style={styles.centeredView}>
      <Modal animationType='slide' transparent={true} visible={visible}>
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
            <Text style={styles.modalText}>Name</Text>
            <TextInput placeholder={'Player ' + playerNumber.toString()}/>
            <Text style={styles.modalText}>Select Your Role</Text>
            <Picker style={{width: 200}} selectedValue={playerRole} 
              onValueChange={(value) => {setPlayerRole(value)}}>
              {getPickerItems(context.mode, context.playerCount)}
            </Picker>
            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: '#2196F3' }}
              onPress={() => { context.players=updatePlayerInfo(context.players, playerNumber, 'Ken', playerRole, setVisible) }}>
              <Text style={styles.textStyle}>Close</Text>
            </TouchableHighlight>
          </View>
        </View>
      </Modal>
    </View>
  )
}

function updatePlayerInfo(playerInfo:PlayerInfo[], playerNumber:number, playerName:string, playerRole:string, setVisible:React.Dispatch<React.SetStateAction<boolean>>) {
  for (let i = 0; i < playerInfo.length; i++) {
    if (playerInfo[i].player == playerNumber) {
      playerInfo[i].name = playerName
      playerInfo[i].role = playerRole
      break
    }
  }
  setVisible(false)
  return playerInfo
}

function getPickerItems(mode:string, playerCount:number) {
  const roles = calculateRoles(mode, playerCount)
  const pickerItems:JSX.Element[] = []
  roles.forEach(role  => {
    if (role.count != 0) {
      pickerItems.push(<Picker.Item key={role.role[0]} label={role.role[0]} value={role.role[0]}/>)
    }
  });
  return pickerItems
}

type Props = {playerIndex: number, visible: boolean, setVisible: React.Dispatch<React.SetStateAction<boolean>>}
type PlayerInfo = {player:number, role:string, name:string}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  openButton: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center'
  },
    modalText: {
    marginBottom: 15,
    textAlign: 'center'
  }
})
