import React, { useContext, useState } from 'react'
import { View } from 'react-native'
import PlayersComponent from '../components/PlayersComponent'
import NavigationBar from '../components/NavigationBar'
import PlayerInfoModal from '../components/PlayerInfoModal'
import { Context } from '../App'
import { ContextType, Player, RoleCount } from '../types/types'
import { calculateRoles } from '../data/Table'

export default function PlayersPage() {
  const context = useContext(Context);
  const [modalVisible, setModalVisible] = useState(false)
  const [playerIndex, setPlayerIndex] = useState(0)
  const [playerRole, setPlayerRole] = useState(context.players[playerIndex].role)
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 9 }}>
        <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
          {PlayersComponent({setModalVisible: setModalVisible, setPlayerIndex, setPlayerRole})}
        </View>
        <View>
          <PlayerInfoModal playerIndex={playerIndex} playerRole={playerRole} setPlayerRole={setPlayerRole} visible={modalVisible} setVisible={setModalVisible}></PlayerInfoModal>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='RolesPage' nextPage='ItemsPage' callback={function() {confirmPlayerRoles(context)}}></NavigationBar>
      </View>
    </View>
  )
}

function confirmPlayerRoles(context: ContextType) {
  const rolesNeeded:RoleCount[] = calculateRoles(context.mode, context.playerCount)
  const rolesEntered:RoleCount[] = []
  rolesNeeded.forEach(rolesNeeded => {
    rolesEntered.push({role: rolesNeeded.role, count:0})
  });
  context.players.forEach(playerInfo => {
    var roleEntered = rolesEntered.find((array) => {return JSON.stringify(array.role) === JSON.stringify([playerInfo.role])})
    if (roleEntered) {
      roleEntered.count += 1
    } else {
      // modal alerting that roles are wrong, double check
    }
  });
  if (rolesEntered === rolesNeeded) {
    // yay
  } else {
    // boo
  }
}
