import { useNavigation } from '@react-navigation/native';
import * as Speech from 'expo-speech'
import React, { useContext, useState } from 'react'
import { View, Text, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App';
import CountdownTimer from '../components/CountdownTimer';
import NavigationBar from '../components/NavigationBar'

export default function DayTimePage() {
  const navigation = useNavigation()
  const appContext = useContext(AppContext)
  const [step, setStep] = useState(0)
  const [timerVisible, setTimerVisible] = useState(false)
  const [buttonVisible, setButtonVisible] = useState(false)
  if (step === 0) {
    Speech.speak(speeches[0], {onDone: () => {
      setTimeout(() => {sequence(step, setStep, navigation, setTimerVisible, setButtonVisible)}, 2 * 1000)
    }})
  }
  return (
    <View style={{ flex: 1 }}>
      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: 'yellow' }}>
        <Text>Day Time of Day {appContext.dayNumber}</Text>
      </View>
      <View style={{ flex: 9 }}>
        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066' }}>
          <Text>{labelTexts[step]}</Text>
          {ShowTimer(timerVisible)}
        </View>
        <View style={{ flex: 1, flexDirection: 'row', backgroundColor: 'white', alignItems: 'center' }}>
          {ShowButton(buttonVisible, () => {sequence(step, setStep, navigation, setTimerVisible, setButtonVisible)})}
          <TouchableHighlight style={{ width: buttonVisible == true ? '50%' : '100%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
            onPress={() => { sequence(step, setStep, navigation, setTimerVisible, setButtonVisible)}}>
            <Text>{buttonTexts[step]}</Text>
          </TouchableHighlight>
        </View>
      </View>
      <View style={{ flex: 1 }}>
        <NavigationBar previousPage='MorningTimePage' nextPage='NightTimePage'></NavigationBar>
      </View>
    </View>
  )
}

function sequence(step:number, setStep:React.Dispatch<any>, navigation:any, setTimerVisible:React.Dispatch<any>, setButtonVisible:React.Dispatch<any>) {
  Speech.stop()
  step += 1
  Speech.speak(speeches[step])
  switch (step) {
    case 2:
      setTimerVisible(true)
      setButtonVisible(true)
      break
    case 3:
      navigation.push('NightTimePage')
      break
  }
  setStep(step)
}

function ShowTimer(timerVisible:boolean) {
  if (timerVisible === true) {
    return (
      <View style={{height: '100%', alignItems: 'center', justifyContent: 'center'}}>
        <CountdownTimer callback={() => {Speech.speak('Time is up.')}}></CountdownTimer>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function ShowButton(buttonVisible:boolean, callback?:() => void) {
  if (buttonVisible === true) {
    return (
      <TouchableHighlight style={{ width: '50%', height: '100%', alignItems: 'center', justifyContent: 'center' }} 
        onPress={() => {  }}>
        <Text>Add 3 minutes</Text>
      </TouchableHighlight>
    )
  } else {
    return (
      <></>
    )
  }

}

const labelText1 = ''
const labelText2 = 'Item or Ability?'
const labelText3 = 'Briefing Time'
const labelTexts = [labelText1, labelText2, labelText3]

const speech1 = '"Mm, ahem, this is a school announcement. It is now the day time. Please make your way to the briefing room."'
const speech2 = 'Would anybody like to use a day time ability or item? Please declare so now. After everyone who wanted to has declared \
their ability or item, click the button below.'
const speech3 = 'Discuss anything you would like, starting now'
const speeches = [speech1, speech2, speech3]

const buttonText1 = ''
const buttonText2 = 'Continue' 
const buttonText3 = 'End Discussion'
const buttonTexts = [buttonText1, buttonText2, buttonText3]
