import React, { useState } from "react";
import { View, Text } from "react-native";
import { Picker } from '@react-native-picker/picker'
import SwitchSelector from "react-native-switch-selector";
import { Context } from "../App";
import NavigationBar from "../components/NavigationBar";

export default function SettingsPage() {
  const [playerCount, setPlayerCount] = useState(4)
  return (
    <Context.Consumer>{(context) => (
      <View style={{flex: 1}}>
        <View style={{flex: 9}}>
          <View style={{flex: 1, alignItems: 'center', justifyContent: 'center', backgroundColor: '#cc0066'}}>
            <View>
              <Text>Mode:</Text>
            </View>
            <View style={{flexDirection: 'row'}}>
              <SwitchSelector
                initial={0}
                onPress={(value) => context.mode=value.toString()}
                textColor='black'
                selectedColor='white'
                buttonColor='#cc0066'
                borderColor='black'
                borderRadius={20}
                hasPadding={true}
                options={[
                  { label: "NORMAL", value: "normal" },
                  { label: "EXTREME", value: "extreme" }
                ]}
              />
            </View>
          </View>
          <View style={{flex: 1, alignItems: 'center', justifyContent: 'center',  backgroundColor: 'white'}}>
            <View>
              <Text>Number of Players:</Text>
            </View>
            <View style={{flexDirection: 'row'}}>
              <Picker
                selectedValue={4}
                // style={{height: 50, width: 100}}
                onValueChange={(value) => context.playerCount=value as number }>
                <Picker.Item label="4" value={4} />
                <Picker.Item label="5" value={5} />
                <Picker.Item label="6" value={6} />
                <Picker.Item label="7" value={7} />
                <Picker.Item label="8" value={8} />
                <Picker.Item label="9" value={9} />
                <Picker.Item label="10" value={10} />
                <Picker.Item label="11" value={11} />
                <Picker.Item label="12" value={12} />
                <Picker.Item label="13" value={13} />
                <Picker.Item label="14" value={14} />
                <Picker.Item label="15" value={15} />
                <Picker.Item label="16" value={16} />
              </Picker>
            </View>
          </View>
        </View>
        <View style={{flex: 1}}>
          <NavigationBar Previous='StartPage' Next='IntroductionPage'></NavigationBar>
        </View>
      </View>
    )}</Context.Consumer>
  )
}
