import React from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'

export default function PlayersComponent({ playerCount, modalVisible, setModalVisible}:Props) {
  const heightPercent = ((1 / Math.ceil(playerCount / 2)) * 100).toString() + '%'
  return (
    <View>
      <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
        {PlayerBoxes(playerCount, heightPercent, setModalVisible)}
      </View>
    </View>
  )
}

function PlayerBoxes(playerCount:number, heightPercent:string, setModalVisible: React.Dispatch<React.SetStateAction<boolean>>) {
  return (
    [...Array(playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={styles(heightPercent).frame}>
        <TouchableOpacity style={styles().button} onPress={() => { setModalVisible(true) }}>
          <Text>Player {index + 1}</Text>
        </TouchableOpacity>
      </View>
    ))
  )
}

type Props = {playerCount: number, modalVisible: boolean, setModalVisible: React.Dispatch<React.SetStateAction<boolean>>}

function styles(heightPercent?: string) {
  return StyleSheet.create({
    centeredView: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: 22
    },
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    },
    button: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'grey'
    }
  })
}
