import React, { useContext } from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'
import { Context } from '../App'

export default function PlayersComponent({setModalVisible, setPlayerIndex, setPlayerRole}:Props) {
  const context = useContext(Context);
  const heightPercent = ((1 / Math.ceil(context.playerCount / 2)) * 100).toString() + '%'
  return (
    [...Array(context.playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={styles(heightPercent).frame}>
        <TouchableOpacity key={'player' + index+1} style={styles().button} onPress={() => {
            setModalVisible(true)
            setPlayerIndex(index)
            setPlayerRole(context.players[index].role)
          }}>
          <Text>{context.players[index].name}</Text>
        </TouchableOpacity>
      </View>
    ))
  )
}

type Props = {setModalVisible: React.Dispatch<any>, setPlayerIndex: React.Dispatch<any>, setPlayerRole: React.Dispatch<any>}

function styles(heightPercent?: string, backgroundColor?: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    },
    button: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: backgroundColor || 'grey'
    }
  })
}
