// import { StatusBar } from 'expo-status-bar'
import React from 'react';
import { StatusBar, View } from 'react-native'
import 'react-native-gesture-handler'
import { NavigationContainer } from '@react-navigation/native'
import { createStackNavigator } from '@react-navigation/stack'

import { AppContextType } from './app/types/types'
import StartScreen from './app/screens/Start'
import SettingsScreen from './app/screens/Settings'
import IntroductionScreen from './app/screens/Introduction'
import RolesScreen from './app/screens/Roles'
import ItemsScreen from './app/screens/Items'
import NightTimeScreen from './app/screens/NightTime'
import PlayersScreen from './app/screens/Players'
import MorningTimeScreen from './app/screens/MorningTime'
import DayTimeScreen from './app/screens/DayTime'

export type RootStackParamList = {
  StartPage: undefined
  SettingsPage: undefined
  IntroductionPage: undefined
  RolesPage: {Mode:string, PlayerCount:number}
  PlayersPage: {PlayerCount:number}
  ItemsPage: {Mode:string}
  NightTimePage: undefined
  MorningTimePage: undefined
  DayTimePage: undefined
};

const RootStack = createStackNavigator<RootStackParamList>();
export const AppContext = React.createContext({} as AppContextType);

export default function App() {
  return (
    <View style={{ flex: 1,  marginTop: StatusBar.currentHeight }}>
      <AppContext.Provider value={{mode: 'normal', playerCount: 4, roleCounts: [], playersInfo: [], dayNumber: 0}}>
        <NavigationContainer>
          <RootStack.Navigator>
            <RootStack.Screen
              name="StartPage"
              component={StartScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="SettingsPage"
              component={SettingsScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="IntroductionPage"
              component={IntroductionScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="RolesPage"
              component={RolesScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="PlayersPage"
              component={PlayersScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="ItemsPage"
              component={ItemsScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="NightTimePage"
              component={NightTimeScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="MorningTimePage"
              component={MorningTimeScreen}
              options={{ headerShown: false }}
            />
            <RootStack.Screen
              name="DayTimePage"
              component={DayTimeScreen}
              options={{ headerShown: false }}
            />
          </RootStack.Navigator>
        </NavigationContainer>
      </AppContext.Provider>
    </View>
  );
}
