import React, { useContext, useState } from 'react'
import { View, Text, StyleSheet, TouchableHighlight } from 'react-native'
import { AppContext } from '../../App'
import ItemCardsModal from './ItemCardsModal';
import PlayerInfoModal from './PlayerInfoModal';

export default function PlayersScreen({modal}:Props) {
  const appContext = useContext(AppContext);
  const [playerIndex, setPlayerIndex] = useState(0)
  const [playerInfoModalVisible, setPlayerInfoModalVisible] = useState(false)
  const [itemCardsModalVisible, setItemCardsModalVisible] = useState(false)
  let setModalVisible:React.Dispatch<any>|undefined = undefined
  if (modal === 'playerInfo') {
    setModalVisible = setPlayerInfoModalVisible
  } else if (modal === 'itemCards') {
    setModalVisible = setItemCardsModalVisible
  }
  return (
    <View>
      <View style={{ height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap' }}>
        {PlayersBoxes(setPlayerIndex, setModalVisible)}
      </View>
      <PlayerInfoModal playerIndex={playerIndex} visible={playerInfoModalVisible} setVisible={setPlayerInfoModalVisible}/>
      <ItemCardsModal playerIndex={playerIndex} visible={itemCardsModalVisible} setVisible={setItemCardsModalVisible}></ItemCardsModal>
    </View>
  )
}

function PlayersBoxes(setPlayerIndex:React.Dispatch<any>, setModalVisible?:React.Dispatch<any>|undefined) {
  const appContext = useContext(AppContext);
  const heightPercent = ((1 / Math.ceil(appContext.playerCount / 2)) * 100).toString() + '%'
  return (
    [...Array(appContext.playerCount)].map((elementInArray, index) => (
      <View key={'player' + index} style={frameStyle(heightPercent).frame}>
        <TouchableHighlight key={'player' + index+1} style={boxStyle(appContext.playersInfo[index].colorScheme).style} onPress={() => {
            if(setModalVisible) { setModalVisible(true) }
            setPlayerIndex(index)
          }}>
          <Text style={textStyle(appContext.playersInfo[index].colorScheme).style}>{appContext.playersInfo[index].name}</Text>
        </TouchableHighlight>
      </View>
    ))
  )
}

type Props = {modal?:string}

function frameStyle(heightPercent: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    }
  })
}

function textStyle(status: string) {
  let textColor = ''
  switch (status) {
    case 'despair':
      textColor = 'white'
      break
    case 'ready':
    case 'notReady':
    case 'hope':
    default:
      textColor = 'black'
      break
  }
  return StyleSheet.create({
    style: {
      color: textColor
    }
  })
}

function boxStyle(status: string) {
  let backgroundColor = ''
  switch (status) {
    case 'ready':
      backgroundColor = 'green'
      break
    case 'despair':
      backgroundColor = 'black'
      break
    case 'notReady':
    case 'hope':
    default:
      backgroundColor = 'white'
      break
  }
  return StyleSheet.create({
    style: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: backgroundColor
    }
  })
}
