import React, { useState } from "react";
import { Alert, Modal, TouchableHighlight, View, Text, StyleSheet } from "react-native";
import { Context } from "../App";
import PlayersComponent from "../components/PlayersComponent";
import NavigationBar from "../components/NavigationBar";

export default function PlayersPage() {
  const [modalVisible, setModalVisible] = useState(false);
  return (
    // <Context.Consumer>{(context) => (
    //   <View style={{flex: 1}}>
    //     <View style={{flex: 9}}>
    //       {PlayersComponent({playerCount: context.playerCount})}
    //     </View>
    //     <View style={{flex: 1}}>
    //       <NavigationBar Previous='RolesPage' Next='ItemsPage'></NavigationBar>
    //     </View>
    //   </View>
    // )}</Context.Consumer>
    <View style={styles.centeredView}>
    <Modal
      animationType="slide"
      transparent={true}
      visible={modalVisible}
      onRequestClose={() => {Alert.alert("Modal has been closed.");}}
    >
      <View style={styles.centeredView}>
        <View style={styles.modalView}>
          <Text style={styles.modalText}>Hello World!</Text>

          <TouchableHighlight
            style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
            onPress={() => {
              setModalVisible(!modalVisible);
            }}
          >
            <Text style={styles.textStyle}>Hide Modal</Text>
          </TouchableHighlight>
        </View>
      </View>
    </Modal>

    <TouchableHighlight
      style={styles.openButton}
      onPress={() => {
        setModalVisible(true);
      }}
    >
      <Text style={styles.textStyle}>Show Modal</Text>
    </TouchableHighlight>
  </View>
  )
}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    marginTop: 22
  },
  modalView: {
    margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 35,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    elevation: 2
  },
  textStyle: {
    color: "white",
    fontWeight: "bold",
    textAlign: "center"
  },
  modalText: {
    marginBottom: 15,
    textAlign: "center"
  }
});
