import React, { useContext, useState } from "react"
import { Modal, View, Text, TouchableHighlight } from "react-native"
import { modalStyles } from "../../styles/styles"
import { Picker } from '@react-native-picker/picker'
import { GameContext } from "../../../App"

export default function MorningTimeAbilitiesItemsModal({visible, setVisible}:Props) {
  const gameContext = useContext(GameContext)
  const [itemCard, setItemCard] = useState('')
  const [playerAbility, setPlayerAbility] = useState('')
  return (
    <View>
      <Modal animationType="slide" transparent={true} visible={visible} >
        <View style={modalStyles.centeredView}>
          <View style={modalStyles.modalView}>
            <Picker style={{width: 250}} selectedValue={playerAbility}
              onValueChange={(value) => {setPlayerAbility(value.toString())}}>
              <Picker.Item key="" label="Select an ability" value=""/>
              <Picker.Item key="Makoto Naegi" label="Makoto Naegi" value="Makoto Naegi"/>
              <Picker.Item key="Leon Kuwata" label="Leon Kuwata" value="Leon Kuwata"/>
              <Picker.Item key="Sakura Ogami" label="Sakura Ogami" value="Sakura Ogami"/>
              <Picker.Item key="Nagito Komaeda" label="Nagito Komaeda" value="Nagito Komaeda"/>
              <Picker.Item key="Akane Owari" label="Akane Owari" value="Akane Owari"/>
            </Picker>
            <Picker style={{width: 250}} selectedValue={itemCard}
              onValueChange={(value) => {setItemCard(value.toString())}}>
              <Picker.Item key="" label="Select an item" value=""/>
              <Picker.Item key="Fragment of Hope" label="Fragment of Hope" value="Fragment of Hope"/>
              <Picker.Item key="Amulet of Takejin" label="Amulet of Takejin" value="Amulet of Takejin"/>
            </Picker>
            <View style={{flexDirection: 'row'}}>
              <TouchableHighlight
                style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
                onPress={() => {
                  setVisible(!visible)
                  if (itemCard === 'Amulet of Takejin') {
                    gameContext.blackenedAttack += 1
                    if (gameContext.blackenedAttack === gameContext.playerCount) {
                      gameContext.blackenedAttack = 0
                    }
                  }
                }}>
                <Text style={modalStyles.textStyle}>Yes</Text>
              </TouchableHighlight>
              <TouchableHighlight
                style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
                onPress={() => {
                  setVisible(!visible) 
                }}>
                <Text style={modalStyles.textStyle}>No</Text>
              </TouchableHighlight>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  )
}

type Props = {visible:boolean, setVisible:React.Dispatch<any>}
