import React, { useContext, useState } from "react";
import { Modal, View, Text, TouchableHighlight } from "react-native";
import { modalStyles } from "../styles/styles";
import { Picker } from '@react-native-picker/picker'
import { AppContext } from "../../App";

export default function ItemCardsModal({playerIndex, modalVisible, setModalVisible}:Props) {
  const appContext = useContext(AppContext);
  const [itemCard, setItemCard] = useState('none')
  return (
    <View>
      <Modal animationType="slide" transparent={true} visible={modalVisible}>
        <View style={modalStyles.centeredView}>
          <View style={modalStyles.modalView}>
            <Text>Select your Item Card</Text>
            <Picker style={{width: 200}} selectedValue={appContext.playersInfo[playerIndex].item}
              onValueChange={(value) => {appContext.playersInfo[playerIndex].item = value.toString()}}>
              <Picker.Item key="Glasses" label="Glasses" value="Glasses"/>
              <Picker.Item key="Someone's Graduation Album" label="Someone's Graduation Album" value="Someone's Graduation Album"/>
              <Picker.Item key="Silent Receiver" label="Silent Receiver" value="Silent Receiver"/>
            </Picker>
            <View style={{flexDirection: 'row'}}>
              <TouchableHighlight
                style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
                onPress={() => { 
                  setModalVisible(!modalVisible)
                  appContext.playersInfo[playerIndex].status = 'ready'
                  appContext.playersInfo[playerIndex].item = itemCard
                }}>
                <Text style={modalStyles.textStyle}>OK</Text>
              </TouchableHighlight>
              <TouchableHighlight
                style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
                onPress={() => { 
                  setModalVisible(!modalVisible) 
                  appContext.playersInfo[playerIndex].status = 'notReady'
                  appContext.playersInfo[playerIndex].item = ''
                }}>
                <Text style={modalStyles.textStyle}>Cancel</Text>
              </TouchableHighlight>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

type Props = {playerIndex: number, modalVisible:boolean, setModalVisible:React.Dispatch<any>}
