import React, { useContext, useState } from "react"
import { Modal, View, Text, TouchableHighlight } from "react-native"
import { AppContext } from "../../App"
import { modalStyles } from "../styles/styles"

export default function RevealRoleModal({visible, setVisible, playerIndex, abilityOrItem, setPlayersPageVisible}:Props) {
  const appContext = useContext(AppContext)
  const [confirmationVisible, setConfirmationVisible] = useState(true)
  const [revelationVisible, setRevelationVisible] = useState(false)
  return (
    <Modal animationType="slide" transparent={true} visible={visible}>
      <View style={modalStyles.centeredView}>
        {Confirmation(confirmationVisible, setConfirmationVisible, setRevelationVisible, setVisible, playerIndex)}
        {Revelation(revelationVisible, playerIndex, abilityOrItem, setPlayersPageVisible)}
      </View>
    </Modal>
  )
}

function Confirmation(visible:boolean, setConfirmationVisible:React.Dispatch<any>, setRevelationVisible:React.Dispatch<any>, setVisible:React.Dispatch<any>, playerIndex:number) {
  const appContext = useContext(AppContext)
  if (visible) {    
    return (
      <View style={modalStyles.modalView}>
        <Text style={modalStyles.modalText}>Investigate  + {appContext.playersInfo[playerIndex].name}?</Text>
        <View style={{flexDirection: 'row'}}>
          <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }}
            onPress={() => { 
              setConfirmationVisible(false)
              setRevelationVisible(true)
            }}>
            <Text style={modalStyles.textStyle}>Yes</Text>
          </TouchableHighlight>
          <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }} 
            onPress={() => { setVisible(false) }}>
            <Text style={modalStyles.textStyle}>No</Text>
          </TouchableHighlight>
        </View>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function Revelation(visible:boolean, playerIndex:number, abilityOrItem:string,  setPlayersPageVisible?:React.Dispatch<any>) {
  const appContext = useContext(AppContext)
  if (visible) {    
    return (
      <View style={modalStyles.modalView}>
        <Text style={modalStyles.modalText}>{revealText(playerIndex, abilityOrItem)}</Text>
        <TouchableHighlight style={{ ...modalStyles.button, backgroundColor: "#2196F3" }} 
          onPress={() => { setPlayersPageVisible!(false) }}>
          <Text style={modalStyles.textStyle}>OK</Text>
        </TouchableHighlight>
      </View>
    )
  } else {
    return (
      <></>
    )
  }
}

function revealText(playerIndex:number, abilityOrItem:string):string {
  const appContext = useContext(AppContext)
  let string = ''
  switch (abilityOrItem) {
    case "Yasuhiro Hagakure's ability":
      const role = appContext.playerCount < 7 ? 'Despair Disease Patient' : 'Monomi'
      string = appContext.playersInfo[playerIndex].role == role ? role : 'Not ' + role
      break
    case "Glasses":
      if (['Blackened', 'Traitor', 'Despair Disease Patient'].includes(appContext.playersInfo[playerIndex].role)) {
        string = 'Despair'
      } else {
        string = 'Hope'
      }
      break
    case "Someone's Graduation Album":
      string = appContext.playersInfo[playerIndex].role == 'Traitor' ? 'Traitor' : 'Not Traitor'
      break
    case "Silent Receiver":
      string = appContext.playersInfo[playerIndex].role == 'Spotless' ? 'Spotless' : 'Not Spotless'
      break
  }
  return string
}

type Props = {visible:boolean, setVisible:React.Dispatch<any>, playerIndex:number, abilityOrItem:string, setPlayersPageVisible?:React.Dispatch<any>}
