import React from "react";
import { View, Text, StyleSheet, TouchableOpacity, Modal, TouchableHighlight } from "react-native";

export default function PlayersComponent({playerCount}:Props) {
  const heightPercent = ((1 / Math.ceil(playerCount / 2)) * 100).toString() + '%'
  return (
    <View style={{height: '100%', width: '100%', flexDirection: 'column', flexWrap: 'wrap'}}>
      {playerBoxes(playerCount, heightPercent)}
    </View>
  )
}

function playerBoxes(playerCount:number, heightPercent:string) {
  const [modalVisible, setModalVisible] = useState(false);
  return (
    <View>
    [...Array(playerCount)].map((elementInArray, index) => (
      <View style={styles(heightPercent).frame}>
        <TouchableOpacity style={styles().button} onPress={() => {setModalVisible(true);}}>
          <Text>Player {index + 1}</Text>
        </TouchableOpacity>
      </View>
    ))
    <View style={styles().centeredView}>
    <Modal
      animationType="slide"
      transparent={true}
      visible={modalVisible}
      onRequestClose={() => {alert.alert("Modal has been closed.");}}
    >
      <View style={styles.centeredView}>
        <View style={styles.modalView}>
          <Text style={styles.modalText}>Hello World!</Text>

          <TouchableHighlight
            style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
            onPress={() => {
              setModalVisible(!modalVisible);
            }}
          >
            <Text style={styles.textStyle}>Hide Modal</Text>
          </TouchableHighlight>
        </View>
      </View>
    </Modal>

    <TouchableHighlight
      style={styles.openButton}
      onPress={() => {setModalVisible(true);}}
    >
      <Text style={styles.textStyle}>Show Modal</Text>
    </TouchableHighlight>
  </View>
  </View>
  )
}

function buttonText(index:number) {
  return "Player " + (index + 1).toString()
}

type Props = {playerCount: number}
let players = []

function createPlayer(index:number) {
  const player = "Player " + index.toString()
  players.push({player:player, role:"Alter Ego"})
}

function styles(heightPercent?: string) {
  return StyleSheet.create({
    frame: {
      width: '50%',
      height: heightPercent,
      borderColor: 'black',
      borderWidth: 5,
      alignItems: 'center',
      justifyContent: 'center'
    },
    button: {
      width: '100%',
      height: '100%',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: 'grey'
    },

    centeredView: {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 22
    },
    modalView: {
      margin: 20,
      backgroundColor: "white",
      borderRadius: 20,
      padding: 35,
      alignItems: "center",
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 3.84,
      elevation: 5
    },
    openButton: {
      backgroundColor: "#F194FF",
      borderRadius: 20,
      padding: 10,
      elevation: 2
    },
    textStyle: {
      color: "white",
      fontWeight: "bold",
      textAlign: "center"
    },
    modalText: {
      marginBottom: 15,
      textAlign: "center"
    }
  });
}
